let imagesProduct=document.querySelectorAll(".product_photo"),mainImage=document.getElementById("main_photo");imagesProduct.forEach(n=>n.addEventListener("click",()=>mainImage.src=n.src));let popUpMenu=document.querySelector(".pop-up_menu"),menuSign=document.querySelector(".pop-up_menu_sign"),menuClose=document.querySelector(".menu_close");menuSign.addEventListener("click",()=>popUpMenu.style.display="block");menuClose.addEventListener("click",()=>popUpMenu.style.display="none");